$(document).ready(function () {
  for (var key in Games) {
    var new_div =
      $(`<div id="csgo">
 <img id="csgoimg" src="`+ Games[key].image + `" alt="test"><d>`+ Games[key].genre + `</d>
 <p class="csgotitle" id="csgotitle">`+ Games[key].name + `</p>
 <p class="description">`+ Games[key].description + `</p>
 <a href="`+ Games[key].link + `"><button class="download">Download Now!</button></a>



 </div>`)
    $("box").append(new_div);
  }
});

$(document).ready(function () {
  $("#searchbar").on("keyup", function () {
    var value = $(this).val().toLowerCase();
    $("#box div ").filter(function () {
      $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
    });
  });
});

